#!/bin/bash\n\n# üéØ Brand Mention Tracker - Hackathon Demo Script\n# This script demonstrates the solution for judges\n\necho \"üéâ Brand Mention Tracker - RapidQuest Hackathon Demo\"\necho \"======================================================\"\necho \"\"\n\n# Check MongoDB connection (Atlas or Local)\necho \"üìä 1. Checking MongoDB configuration...\"\nif [ -f \"backend/.env\" ] && grep -q \"mongodb+srv\" backend/.env; then\n    echo \"‚úÖ MongoDB Atlas configuration detected\"\n    echo \"   Using cloud database (recommended)\"\nelif [ -f \"backend/.env\" ] && grep -q \"mongodb://localhost\" backend/.env; then\n    echo \"‚úÖ Local MongoDB configuration detected\"\n    if ! pgrep -x \"mongod\" > /dev/null; then\n        echo \"‚ö†Ô∏è  Local MongoDB not running. Please start it:\"\n        echo \"   brew services start mongodb/brew/mongodb-community\"\n    fi\nelse\n    echo \"‚ö†Ô∏è  No MongoDB configuration found\"\n    echo \"   Please follow API_KEYS_GUIDE.md to set up MongoDB Atlas\"\nfi\n\necho \"\"\necho \"üîß 2. Checking environment configuration...\"\nif [ -f \"backend/.env\" ]; then\n    echo \"‚úÖ Backend environment configured\"\n    # Check for API keys\n    if grep -q \"OPENAI_API_KEY=sk-\" backend/.env; then\n        echo \"‚úÖ OpenAI API key configured\"\n    else\n        echo \"‚ö†Ô∏è  OpenAI API key not configured (demo will use fallback)\"\n    fi\n    \n    if grep -q \"TWITTER_BEARER_TOKEN=\" backend/.env; then\n        echo \"‚úÖ Twitter API configured\"\n    else\n        echo \"‚ö†Ô∏è  Twitter API not configured (demo data will be simulated)\"\n    fi\nelse\n    echo \"‚ö†Ô∏è  Backend .env not found, copying from example...\"\n    cp backend/.env.example backend/.env\nfi\n\nif [ -f \"frontend/.env\" ]; then\n    echo \"‚úÖ Frontend environment configured\"\nelse\n    echo \"‚ö†Ô∏è  Frontend .env not found, copying from example...\"\n    cp frontend/.env.example frontend/.env\nfi\n\necho \"\"\necho \"üì¶ 3. Installing dependencies...\"\nnpm install --silent\ncd backend && npm install --silent && cd ..\ncd frontend && npm install --silent && cd ..\n\necho \"\"\necho \"üöÄ 4. Starting the application...\"\necho \"   - Backend API: http://localhost:5000/api\"\necho \"   - Frontend Dashboard: http://localhost:3000\"\necho \"   - Health Check: http://localhost:5000/api/health\"\necho \"\"\necho \"‚è∞ Starting servers in 3 seconds...\"\nsleep 3\n\n# Start the application\nnpm run dev