#!/bin/bash

# Brand Mention Tracker Setup Script
echo \"üöÄ Setting up Brand Mention Tracker...\"\n\n# Check if MongoDB is running\necho \"üìä Checking MongoDB...\"\nif ! pgrep -x \"mongod\" > /dev/null; then\n    echo \"‚ö†Ô∏è  MongoDB is not running. Please start MongoDB first:\"\n    echo \"   brew services start mongodb/brew/mongodb-community\"\n    echo \"   # OR\"\n    echo \"   mongod --config /usr/local/etc/mongod.conf\"\n    exit 1\nfi\n\n# Install root dependencies\necho \"üì¶ Installing root dependencies...\"\nnpm install\n\n# Install backend dependencies\necho \"üì¶ Installing backend dependencies...\"\ncd backend\nnpm install\ncd ..\n\n# Install frontend dependencies\necho \"üì¶ Installing frontend dependencies...\"\ncd frontend\nnpm install\ncd ..\n\n# Copy environment files\necho \"‚öôÔ∏è  Setting up environment files...\"\ncp backend/.env.example backend/.env\ncp frontend/.env.example frontend/.env\n\necho \"‚úÖ Setup complete!\"\necho \"\"\necho \"üîß Next Steps:\"\necho \"1. Edit backend/.env with your API keys:\"\necho \"   - OPENAI_API_KEY=your_openai_key\"\necho \"   - TWITTER_BEARER_TOKEN=your_twitter_token\"\necho \"   - REDDIT_CLIENT_ID=your_reddit_id\"\necho \"   - REDDIT_CLIENT_SECRET=your_reddit_secret\"\necho \"   - GOOGLE_NEWS_API_KEY=your_news_api_key\"\necho \"\"\necho \"2. Start the application:\"\necho \"   npm run dev\"\necho \"\"\necho \"üìä The dashboard will be available at:\"\necho \"   Frontend: http://localhost:3000\"\necho \"   Backend API: http://localhost:5000/api\"\necho \"   Health Check: http://localhost:5000/api/health\"